import stories from './storiesDB';

function ViewStory() {
    const [storyIndex, setStoryIndex] = useState(0);
    // const [story, setStory] = useState(stories[0].body);

    // const handleNextStory = () => {
    //     setStory(stories[1].body);
    // }

    const handleNextStory = () => {

        setStoryIndex((prevStoryIndex => {
            if(prevStoryIndex < stories.length){
                prevStoryIndex + 1;  
            }

        }));
    }

  return (
    <div>
        <br/>
        {/* <button onClick={handleNextStory}>Next Story</button>
        <p>{story}</p> */}
        <button onClick={handleNextStory}>Next story</button>
        <p>{storyIndex}</p>
    </div>
  );
}

export default ViewStory;
