                rows="4" 
                cols="50" 
                placeholder='Type Story Here'
                value={storyBody}
                onChange = {(e) => {setStoryBody(e.target.value)}}
                required></textarea>
            <button>Ready To Publish!</button>
        </form>
        <p>{storyBody}</p>
    </div>
  )
}

export default TextBox;
