import React, { useState } from 'react';
import stories from './storiesDB';

function ViewStory() {
    const [storyIndex, setStoryIndex] = useState(0);
    const [story, setStory] = useState(stories[storyindex].body)

    const nextStory = () => {
        setStory(storyIndex+1);
    }


  return (
    <div>
        <br/>
        <button onClick = {nextStory}>Next Story!</button>
        <p>{story}</p>
    </div>
  );
}

export default ViewStory;
