import React, { useState } from 'react';

function TextBox() {

    const [user, setUser] = useState('');
    const [title, setTitle] = useState('');
    const [storyBody, setStoryBody] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault()
        try {
            let res = await fetch("http://localhost:4000/stories", {
                method: "POST",
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    user: user,
                    title: title,
                    body: storyBody
                })
            });
            await res.json();
            console.log()
            if(res.status === 200){
                setUser("");
                setTitle("");
                setStoryBody("");
            }
            else{
                console.log("Error occured in handleSubmit function");
            }
        } catch (error) {
            console.log(error);
        }
    }

  return (
    <div>
        <form onSubmit={handleSubmit} >
            <label for="title" id="title">Title:</label>
            <input 
                id="title" 
                type="text" 
                name="title"
                value={title}
                required
                onChange={(e) => setTitle(e.target.value)}
            />

            <label for="userName" id="userName">User Name:</label>
            <input 
                id="userName" 
                type="text" 
                name="userName" 
                value={user}
                onChange={(e) => setUser(e.target.value)}
                required
            />

            <textarea 
                id="storyBody" 
                rows="4" 
                cols="50" 
                placeholder='Type Story Here'
                value={storyBody}
                onChange = {(e) => setStoryBody(e.target.value)}
                required></textarea>
            <button>Ready To Publish!</button>
        </form>
    </div>
  )
}

export default TextBox;
