import React, { useState } from 'react';
import viewStoriesCSS from './viewStories'

function ViewStories(props) {

    const [story, setStory] = useState([]);
    const url = "http://localhost:4000/" + props.artURL;
    const viewStoryBut = document.getElementById('viewStoryButton');

    // GET story
    const getStory = () => {
        fetch(url)
        .then(res => res.json())
        .then(data => {
            console.log(data);
            let array = data.map(ob => <p key={ob._id}>{ob.body}</p>)
            setStory(array);
        })
        viewStoryBut.style.display = 'none';
      }

      const handleDisplay = () => {
        viewStoryBut.style.display = 'block';
      }

  return (
    <div>
        <button onClick={getStory}>Start!</button>
        <button onClick={handleDisplay} id = "finishButton">Finished!</button>
        {/* insert new displayStoriesComponent here */}
        <div>{story[0]}</div>
    </div>
  )
}

export default ViewStories;
