import React, { useState } from 'react';
import viewStoriesCSS from './viewStories.css'

function ViewStories(props) {

    const [story, setStory] = useState([]);
    const url = "http://localhost:4000/" + props.artURL;
    let imageSRC;
    let imageID;

    // GET story
    const getStory = () => {
        fetch(url)
        .then(res => res.json())
        .then(data => {
            console.log(data);
            let array = data.map(ob => <p key={ob._id}>{ob.body}</p>)
            setStory(array);
        })
      }

      if(props.artURL === "Green-Lake-Stories"){
        imageSRC = "./images/The_Refuge.jpg";
        imageID = "Green_Lake_Viewing";
      }
      else if(props.artURL === "Ghibli-House-Stories"){
        imageSRC = "./images/Ghibili_House.jpg";
        imageID = "Ghibli_House_Viewing";
      }
      else if(props.artURL === "Carosel-Stories"){
        imageURL = "./images/Carosel_Path.jpg";
        imageID = "Carosel_Viewing";
      }
      else {
        imageURL = "./images/Cooling_Mist.jpg";
        imageID = "Cooling_Mist_Viewing";
      }

  return (
    <div>
        <img src={imageURL} id = {imageID}></img>
        <button onClick={getStory}>Let's Begin!</button>
        {/* insert new displayStoriesComponent here */}
        <div>{story}</div>
    </div>
  )
}

export default ViewStories;
