import React, { useEffect, useState } from 'react';
import stories from './storiesDB';
import NavStoryCSS from './NavStory.module.css'

function NavStory() {

    const [storyIndex, setStoryIndex] = useState(0);
    let story = stories[storyIndex].body;
    let author = stories[storyIndex].user;

    //Next Story Button
    const handleNextStory = () => {
        if(storyIndex < stories.length-1){
            setStoryIndex(prevStoryIndex => prevStoryIndex + 1);
        }
    }

    //Prev Story Button
    const handlePrevStory = () => {
        if(storyIndex > 0){
            setStoryIndex((prevStoryIndex) => prevStoryIndex - 1);
        }
    }

    useEffect(() => {
        story = stories[storyIndex].body;
        author = stories[storyIndex].user;
    }, [storyIndex]);

  return (
    <div>
        <br/>
        <button className="btn" onClick = {handlePrevStory}>Prev Story</button>
        <button className = "btn" onClick={handleNextStory}>Next story</button>

        <p>{storyIndex + 1} of {stories.length}</p>
        <p>{story}</p>
        <p>By: {author}</p>
    </div>
  );
}

export default NavStory;
