    const [title, setTitle] = useState('');
    const [storyBody, setStoryBody] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault()

        fetch("http://localhost:4000/stories", {
            method: "POST",
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
